CREATE VIEW monto_usado_solicitud_pedido AS SELECT sp.fecha_de_pedido_id,i.centro_de_costo_id,SUM(dsp.estimated_price) AS monto_usado FROM detalle_de_solicitud_de_pedidos dsp INNER JOIN solicitud_de_pedidos sp ON sp.id = dsp.solicitud_de_pedido_id INNER JOIN implementos i ON i.id = sp.implemento_id GROUP BY i.centro_de_costo_id,sp.fecha_de_pedido_id;