CREATE VIEW vista_reporte_de_tractores AS
SELECT p.sede_id,s.sede,p.fecha,p.turno,f.fundo,l.lote,r.correlativo,u.name, CONCAT(mt.modelo_de_tractor,' ',t.numero) AS tractor, r.horometro_inicial,r.horometro_final, CONCAT(mi.modelo_de_implemento,' ',i.numero) AS implemento, la.labor FROM programacion_de_tractors p INNER JOIN lotes l ON l.id = p.lote_id INNER JOIN fundos f ON f.id = l.fundo_id INNER JOIN reporte_de_tractors r ON r.programacion_de_tractor_id = p.id INNER JOIN users u ON u.id = p.tractorista INNER JOIN tractors t ON t.id = p.tractor_id INNER JOIN modelo_de_tractors mt ON mt.id = t.modelo_de_tractor_id INNER JOIN implementos i ON i.id = p.implemento_id INNER JOIN modelo_del_implementos mi ON mi.id = i.modelo_del_implemento_id INNER JOIN labors la ON la.id = p.labor_id INNER JOIN sedes s ON s.id = p.sede_id;